import React, { Component } from 'react';
import {Form,Card,Button,Col} from 'react-bootstrap';

class AddBook extends Component {

  constructor(props){
    super(props);
    this.state={title:'',author:'',isbn:'',cost:''};
    this.bookChange = this.bookChange.bind(this);
    this.submitBook = this.submitBook.bind(this);
  }

  submitBook(event){
    alert(this.state.title);
    event.preventDefault();

  }

  bookChange(event){
    this.setState({
      [event.target.name]:event.target.value
    });
  }

  render() {
    return (
      <Card className={"border border-dark bg-dark text-white"}>
        <Card.Header>Book List</Card.Header>
        <Form id="bookFormId">
          <Card.Body>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridTitle">
                <Form.Label>Title</Form.Label>
                <Form.Control required name="title" value={this.state.title} onChange={this.bookChange} type="test" placeholder="Enter Title" className={"bg-dark text-white"}/>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Author</Form.Label>
                <Form.Control name="author" type="text" placeholder="Enter Author" className={"bg-dark text-white"}/>
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col}>
                <Form.Label>ISBN</Form.Label>
                <Form.Control name="isbn" type="text" placeholder="Enter ISBN" className={"bg-dark text-white"}/>
              </Form.Group>
              <Form.Group as={Col}>
                <Form.Label>Cost</Form.Label>
                <Form.Control name="cost" type="text" placeholder="Enter Cost" className={"bg-dark text-white"}/>
              </Form.Group>
            </Form.Row>
            </Card.Body>
            <Card.Footer>
              <Button variant="success" type="submit">
                Submit
              </Button>
          </Card.Footer>
        </Form>
      </Card>
    );
  }

}

export default AddBook;
