import React, { Component } from 'react';
import {Card,Table,ButtonGroup,Button} from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faList,faEdit,faTrash} from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

class BookList extends Component {

  constructor(props){
    super(props);
    this.state={
      books:[]
    };
  }

  componentDidMount(){
    axios.get('http://localhost:8080/api/books')
    .then((res)=>{
      this.setState({
        books: res.data
      })
    });
  }

  deleteItem=(event,index)=>{
    event.preventDefault();
    alert('Deleted '+index);
  }

  updateItem=(event)=>{
    event.preventDefault();
    alert('Updated');
  }

  render() {
    return (
      <Card className={"border border-dark bg-dark text-white"}>
        <Card.Header><FontAwesomeIcon icon={faList}/>Book List</Card.Header>
        <Card.Body>
          <Table bordered hover striped variant="dark">
            <thead>
              <tr>
                <th>#</th>
                <th>Title</th>
                <th>Author</th>
                <th>ISBN Number</th>
                <th>Price</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {

                this.state.books.map((book,index)=>
                  <tr>
                    <td key={index}>{index+1}</td>
                    <td>{book.name}</td>
                    <td>{book.author}</td>
                    <td>{book.isbn}</td>
                    <td>{book.cost}</td>
                    <td>
                      <ButtonGroup>
                        <Button size="sm" onClick={this.updateItem} variant="outline-primary"><FontAwesomeIcon icon={faEdit}/></Button>{' '}
                        <Button size="sm" onClick={this.deleteItem.bind({index})} variant="outline-primary"><FontAwesomeIcon icon={faTrash}/></Button>
                      </ButtonGroup>

                    </td>
                  </tr>
                )
              }
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    );
  }

}

export default BookList;
