import React, { Component } from 'react';
import {Form,Card,Button,Col} from 'react-bootstrap';
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import {faSave,faPlusSquare} from '@fortawesome/free-solid-svg-icons'

class AddBook extends Component {

  constructor(props){
    super(props);
    this.state={title:'',author:'',isbn:'',cost:''};
    this.bookChange = this.bookChange.bind(this);
    this.submitBook = this.submitBook.bind(this);
  }

  submitBook(event){
    event.preventDefault();
    var book = {name: this.state.title, author: this.state.author, isbn: this.state.isbn, cost:this.state.cost};
    axios({
      method: 'post',
      url: 'http://localhost:8080/api/books',
      data: book
    })
    .then((res)=>{
      console.log(res)
    })
    .catch((err)=>{
      console.log(err)
    });

  }

  bookChange(event){
    this.setState({
      [event.target.name]:event.target.value
    });
  }

  render() {
    return (
      <Card className={"border border-dark bg-dark text-white"}>
        <Card.Header><FontAwesomeIcon icon={faPlusSquare}/>Add Book</Card.Header>
        <Form onSubmit={this.submitBook} id="bookFormId">
          <Card.Body>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridTitle">
                <Form.Label>Title</Form.Label>
                <Form.Control required name="title" value={this.state.title} onChange={this.bookChange} type="text" placeholder="Enter Title" className={"bg-dark text-white"}/>
              </Form.Group>
              <Form.Group as={Col} controlId="formGridAuthor">
                <Form.Label>Author</Form.Label>
                <Form.Control required name="author" value={this.state.author} onChange={this.bookChange} name="author" type="text" placeholder="Enter Author" className={"bg-dark text-white"}/>
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} controlId="formGridISBN">
                <Form.Label>ISBN</Form.Label>
                <Form.Control required name="isbn" value={this.state.isbn} onChange={this.bookChange} name="isbn" type="text" placeholder="Enter ISBN" className={"bg-dark text-white"}/>
              </Form.Group>
              <Form.Group as={Col} controlId="formGridCost">
                <Form.Label>Cost</Form.Label>
                <Form.Control required name="cost" value={this.state.cost} onChange={this.bookChange} name="cost" type="text" placeholder="Enter Cost" className={"bg-dark text-white"}/>
              </Form.Group>
            </Form.Row>
            </Card.Body>
            <Card.Footer>
              <Button variant="success" type="submit">
                <FontAwesomeIcon icon={faSave}/>Submit
              </Button>
          </Card.Footer>
        </Form>
      </Card>
    );
  }

}

export default AddBook;
